import React from 'react';
import { Box, Typography, Paper, List, ListItem, ListItemText, Divider } from '@mui/material';
import { Patient } from '../types';
import { useClinic } from '../context/ClinicContext';

const maskName = (name: string): string => {
  if (name.length <= 2) {
    return name.charAt(0) + '○';
  }
  return name.charAt(0) + '○'.repeat(name.length - 2) + name.charAt(name.length - 1);
};

const PublicView: React.FC = () => {
  const { state } = useClinic();
  const { patients } = state;

  const getStatusText = (status: Patient['status']): string => {
    switch (status) {
      case 'waiting':
        return '候診中';
      case 'inProgress':
        return '看診中';
      case 'completed':
        return '完診';
      case 'noShow':
        return '過號';
      default:
        return '';
    }
  };

  // 如果沒有病人數據，顯示一些模擬數據
  const displayPatients = patients.length > 0 ? patients : [
    {
      id: '1',
      name: '王小明',
      queueNumber: 1,
      status: 'inProgress',
      checkInTime: new Date(),
    },
    {
      id: '2',
      name: '張三',
      queueNumber: 2,
      status: 'waiting',
      checkInTime: new Date(),
    },
    {
      id: '3',
      name: '歐陽妮妮',
      queueNumber: 3,
      status: 'noShow',
      checkInTime: new Date(),
      noShowTime: new Date(),
    },
  ];

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        診所叫號系統
      </Typography>
      
      <Paper sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          目前看診狀態
        </Typography>
        <List>
          {displayPatients.map((patient) => (
            <React.Fragment key={patient.id}>
              <ListItem>
                <ListItemText
                  primary={`${patient.queueNumber}號 - ${maskName(patient.name)}`}
                  secondary={getStatusText(patient.status)}
                />
              </ListItem>
              <Divider />
            </React.Fragment>
          ))}
        </List>
      </Paper>

      <Paper sx={{ p: 2 }}>
        <Typography variant="h6" gutterBottom>
          過號病人預計看診順序
        </Typography>
        <List>
          {displayPatients
            .filter((p) => p.status === 'noShow')
            .map((patient) => (
              <ListItem key={patient.id}>
                <ListItemText
                  primary={`${patient.queueNumber}號 - ${maskName(patient.name)}`}
                  secondary="預計順序: 9-->5-->10-->3-->11"
                />
              </ListItem>
            ))}
        </List>
      </Paper>
    </Box>
  );
};

export default PublicView;
