import React from 'react';
import {
  Box,
  Typography,
  Paper,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  Divider,
  Chip,
} from '@mui/material';
import {
  PlayArrow as PlayArrowIcon,
  Stop as StopIcon,
} from '@mui/icons-material';
import { Patient } from '../types';
import { useClinic } from '../context/ClinicContext';

const DoctorView: React.FC = () => {
  const { state, dispatch } = useClinic();
  const { patients } = state;
  
  // 獲取當前正在看診的病人
  const inProgressPatient = patients.find(p => p.status === 'inProgress');
  
  // 使用 inProgressPatient 作為當前病人，而不是本地狀態
  // 這樣可以確保與全局狀態同步
  const currentPatient = inProgressPatient || null;
  
  // 獲取候診中的病人，按照過號邏輯排序
  const waitingPatients = patients
    .filter(p => p.status === 'waiting')
    .sort((a, b) => {
      // 如果當前沒有正在看診的病人，按照號碼排序
      if (!currentPatient) {
        return a.queueNumber - b.queueNumber;
      }
      
      const currentQueueNumber = currentPatient.queueNumber;
      
      // 如果兩個病人都不是過號病人，按照號碼排序
      if (!a.returnTime && !b.returnTime) {
        return a.queueNumber - b.queueNumber;
      }
      
      // 如果只有一個是過號病人
      if (a.returnTime && !b.returnTime) {
        // 過號病人排在當前號碼之後的兩個號碼之後
        // 如果 b 的號碼小於等於當前號碼，則 a 排在 b 後面
        if (b.queueNumber <= currentQueueNumber) {
          return 1; // a 排在 b 後面
        }
        // 如果 b 的號碼大於當前號碼，則 a 排在 b 前面
        return -1; // a 排在 b 前面
      }
      if (!a.returnTime && b.returnTime) {
        // 過號病人排在當前號碼之後的兩個號碼之後
        // 如果 a 的號碼小於等於當前號碼，則 a 排在 b 前面
        if (a.queueNumber <= currentQueueNumber) {
          return -1; // a 排在 b 前面
        }
        // 如果 a 的號碼大於當前號碼，則 a 排在 b 後面
        return 1; // a 排在 b 後面
      }
      
      // 如果兩個都是過號病人，按照號碼排序
      return a.queueNumber - b.queueNumber;
    });

  // 重新排序候診名單，確保過號病人在報到後排在當前看診號碼之後的兩個號碼之後
  const sortedWaitingPatients = [...waitingPatients].sort((a, b) => {
    if (!currentPatient) {
      return 0; // 保持原有排序
    }
    
    const currentQueueNumber = currentPatient.queueNumber;
    
    // 如果 a 是過號病人且已報到
    if (a.returnTime) {
      // 找到當前看診號碼之後的下一個正常號碼
      const nextNormalNumber = currentQueueNumber + 1;
      
      // 如果 b 的號碼是 nextNormalNumber，則 b 排在 a 前面
      if (b.queueNumber === nextNormalNumber) {
        return 1; // a 排在 b 後面
      }
    }
    
    // 如果 b 是過號病人且已報到
    if (b.returnTime) {
      // 找到當前看診號碼之後的下一個正常號碼
      const nextNormalNumber = currentQueueNumber + 1;
      
      // 如果 a 的號碼是 nextNormalNumber，則 a 排在 b 前面
      if (a.queueNumber === nextNormalNumber) {
        return -1; // a 排在 b 前面
      }
    }
    
    return 0; // 保持原有排序
  });

  const handleStartConsultation = () => {
    // 如果已經有病人正在看診，不做任何操作
    if (currentPatient) return;
    
    // 找到第一個等待中的病人
    const nextPatient = sortedWaitingPatients[0];
    if (!nextPatient) return;
    
    // 更新病人狀態為看診中
    const updatedPatient = {
      ...nextPatient,
      status: 'inProgress' as const,
    };
    dispatch({ type: 'UPDATE_PATIENT', payload: updatedPatient });
  };

  const handleEndConsultation = () => {
    if (!currentPatient) return;

    // 更新病人狀態為完診
    const updatedPatient = {
      ...currentPatient,
      status: 'completed' as const,
    };
    dispatch({ type: 'UPDATE_PATIENT', payload: updatedPatient });
  };

  const handleMarkNoShow = (patientId: string) => {
    const patient = patients.find(p => p.id === patientId) || currentPatient;
    if (patient) {
      const updatedPatient = {
        ...patient,
        status: 'noShow' as const,
        noShowTime: new Date(),
      };
      dispatch({ type: 'UPDATE_PATIENT', payload: updatedPatient });
    }
  };

  const getStatusChip = (status: Patient['status']) => {
    const statusConfig = {
      waiting: { label: '候診中', color: 'default' },
      inProgress: { label: '看診中', color: 'primary' },
      completed: { label: '完診', color: 'success' },
      noShow: { label: '過號', color: 'error' },
    };

    const config = statusConfig[status];
    return (
      <Chip
        label={config.label}
        color={config.color as any}
        size="small"
      />
    );
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        醫師看診系統
      </Typography>

      <Paper sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          目前看診狀態
        </Typography>
        {currentPatient ? (
          <Box sx={{ mb: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              正在看診：
            </Typography>
            <Paper variant="outlined" sx={{ p: 2 }}>
              <Typography variant="h5">
                {currentPatient.queueNumber}號 - {currentPatient.name}
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<StopIcon />}
                  onClick={handleEndConsultation}
                  sx={{ mr: 1 }}
                >
                  結束看診
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={() => handleMarkNoShow(currentPatient.id)}
                >
                  標記過號
                </Button>
              </Box>
            </Paper>
          </Box>
        ) : (
          <Button
            variant="contained"
            color="primary"
            startIcon={<PlayArrowIcon />}
            onClick={handleStartConsultation}
            disabled={sortedWaitingPatients.length === 0}
          >
            開始看診
          </Button>
        )}
      </Paper>

      <Paper sx={{ p: 2 }}>
        <Typography variant="h6" gutterBottom>
          候診名單 ({sortedWaitingPatients.length}人)
        </Typography>
        <List>
          {sortedWaitingPatients.map((patient) => (
            <React.Fragment key={patient.id}>
              <ListItem>
                <ListItemText
                  primary={`${patient.queueNumber}號 - ${patient.name}`}
                  secondary={`病歷號: ${patient.medicalRecordNumber || '無'} ${patient.returnTime ? '(過號已報到)' : ''}`}
                />
                <ListItemSecondaryAction>
                  {getStatusChip(patient.status)}
                </ListItemSecondaryAction>
              </ListItem>
              <Divider />
            </React.Fragment>
          ))}
        </List>
      </Paper>
    </Box>
  );
};

export default DoctorView;
