import React, { useState } from 'react';
import {
  Box,
  Typography,
  Paper,
  Button,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  Divider,
  Chip,
} from '@mui/material';
import {
  PlayArrow as PlayArrowIcon,
  Stop as StopIcon,
  SkipNext as SkipNextIcon,
} from '@mui/icons-material';
import { Patient } from '../types';

const DoctorView: React.FC = () => {
  const [patients, setPatients] = useState<Patient[]>([]);
  const [currentPatient, setCurrentPatient] = useState<Patient | null>(null);

  const handleStartConsultation = () => {
    if (patients.length === 0) return;
    
    const nextPatient = patients[0];
    setCurrentPatient(nextPatient);
    setPatients((prev) => prev.slice(1));
  };

  const handleEndConsultation = () => {
    if (!currentPatient) return;

    setCurrentPatient((prev) => {
      if (prev) {
        const updatedPatient = { ...prev, status: 'completed' as const };
        setPatients((p) => [...p, updatedPatient]);
      }
      return null;
    });
  };

  const handleMarkNoShow = (patientId: string) => {
    setPatients((prev) =>
      prev.map((p) =>
        p.id === patientId
          ? {
              ...p,
              status: 'noShow',
              noShowTime: new Date(),
            }
          : p
      )
    );
  };

  const getStatusChip = (status: Patient['status']) => {
    const statusConfig = {
      waiting: { label: '候診中', color: 'default' },
      inProgress: { label: '看診中', color: 'primary' },
      completed: { label: '完診', color: 'success' },
      noShow: { label: '過號', color: 'error' },
    };

    const config = statusConfig[status];
    return (
      <Chip
        label={config.label}
        color={config.color as any}
        size="small"
      />
    );
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        醫師看診系統
      </Typography>

      <Paper sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          目前看診狀態
        </Typography>
        {currentPatient ? (
          <Box sx={{ mb: 2 }}>
            <Typography variant="subtitle1" gutterBottom>
              正在看診：
            </Typography>
            <Paper variant="outlined" sx={{ p: 2 }}>
              <Typography variant="h5">
                {currentPatient.queueNumber}號 - {currentPatient.name}
              </Typography>
              <Box sx={{ mt: 2 }}>
                <Button
                  variant="contained"
                  color="primary"
                  startIcon={<StopIcon />}
                  onClick={handleEndConsultation}
                  sx={{ mr: 1 }}
                >
                  結束看診
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={() => handleMarkNoShow(currentPatient.id)}
                >
                  標記過號
                </Button>
              </Box>
            </Paper>
          </Box>
        ) : (
          <Button
            variant="contained"
            color="primary"
            startIcon={<PlayArrowIcon />}
            onClick={handleStartConsultation}
            disabled={patients.length === 0}
          >
            開始看診
          </Button>
        )}
      </Paper>

      <Paper sx={{ p: 2 }}>
        <Typography variant="h6" gutterBottom>
          候診名單 ({patients.length}人)
        </Typography>
        <List>
          {patients.map((patient) => (
            <React.Fragment key={patient.id}>
              <ListItem>
                <ListItemText
                  primary={`${patient.queueNumber}號 - ${patient.name}`}
                  secondary={`病歷號: ${patient.medicalRecordNumber || '無'}`}
                />
                <ListItemSecondaryAction>
                  {getStatusChip(patient.status)}
                </ListItemSecondaryAction>
              </ListItem>
              <Divider />
            </React.Fragment>
          ))}
        </List>
      </Paper>
    </Box>
  );
};

export default DoctorView;
